document.getElementById("boton_test").addEventListener("click",()=>{let e=document.getElementById("entrada_texto").value,t=document.getElementById("entrada_expresion").value,n=document.getElementById("resultado");try{let a=t.match(/^\/(.+)\/([gimsuy]*)$/),d=a?new RegExp(a[1],a[2]||"g"):new RegExp(t,"g"),c=e.match(d);n.value=c?`Coincidencias: ${c.length}`:"Sin coincidencias"}catch(e){n.value="Expresión inválida"}});